import React, { useState, useContext } from "react";
import { UserContext } from "../context/UserContext";
import { LoginContext } from "../context/LoginContext";
import styles from "./Chat.module.css";
const Chat = () => {
  const [serverList, setServerList] = useState([
    {
      id: 1,
      name: "Public",
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT8479Ucg3dNkErrscpa3BemM8RUp9Z68HV7w4TJpEL1meuvgETwTvYdJXWm5A1cqkcFA8&usqp=CAU",
    },
    {
      id: 2,
      name: "Fortnite",
      image: "https://i.imgur.com/13RZOhj.gif",
    },
  ]);
  const { isLoggedIn, setIsLoggedIn } = useContext(LoginContext);
  const { loginInfo, setLoginInfo } = useContext(UserContext);
  const [show, setShow] = useState(false);
  const handleMouseEnter = (e) => {
    setShow(true);
  };
  const handleMouseLeave = (e) => {
    setShow(false);
  };
  return (
    <div className={styles.chat}>
      <h2 className={styles.logo}>DISCORD</h2>
      <div className={styles.insideapp}>
        <div className={styles.server}>
          {serverList.map((server) => {
            return (
              <div className={styles.serverIcon} id={server.id} key={server.id}>
                <img
                  onMouseEnter={(e) => handleMouseEnter(e)}
                  onMouseLeave={(e) => handleMouseLeave(e)}
                  src={server.image}
                  alt=""
                />
                <p className={show ? styles.serverIconName : styles.showNone}>
                  {server.name}
                </p>
              </div>
            );
          })}
        </div>
        <div className={styles.leftside}></div>
        <div className={styles.middle}></div>
        <div className={styles.rightside}></div>
      </div>
    </div>
  );
};

export default Chat;
