import React, { useState, useContext } from "react";
import { UserContext } from "../context/UserContext";
import "./Login.css";
import Qr from "../images/qr.png";
import axios from "axios";

const Login = () => {
  const User = useContext(UserContext);
  const [info, setInfo] = useState({ email: "", password: "" });
  const handleLogin = async () => {
    if (info.email === "" || info.password === "") {
      return;
    } else {
      await axios
        .get(`http://localhost:4000/${info.email}`)
        .then((res) => {
          const { data } = res;
          if (
            info.email === data[0].email &&
            info.password === data[0].password
          ) {
            User[info] = info;
          }
        })
        .catch((error) => {
          console.log(error);
        });
    }
  };

  return (
    <div className="Login">
      <form className="loginform">
        <div className="leftside">
          <div className="logingreet">
            <h2 className="loginTitle">Welcome back!</h2>
            <p>We're excited to see you again!</p>
          </div>
          <div className="email">
            <label htmlFor="">EMAIL OR PHONE NUMBER</label>
            <input
              required
              onChange={(e) => {
                const newInfo = info;
                newInfo["email"] = e.target.value;
                setInfo(newInfo);
              }}
              type="email"
            />
          </div>
          <div className="password">
            <label htmlFor="">PASSWORD</label>
            <input
              required
              onChange={(e) => {
                const newInfo = info;
                newInfo["password"] = e.target.value;
                setInfo(newInfo);
              }}
              type="text"
            />
            <a href="#">Forgot your password?</a>
          </div>
          <div className="submitinfo">
            <button onClick={handleLogin} className="submitbottom">
              Login
            </button>
            <p className="register">
              Need an account? <a href="#">Register</a>
            </p>
          </div>
        </div>
        <div className="rightside">
          <div className="content">
            <img className="Qrpng" src={Qr} alt="" />
            <div className="contenttext">
              <h3>Log in wth QR Code</h3>
              <p>
                Scan this with the Discord mobile <br />
                app to log in instantly.
              </p>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Login;
