import React, { useState, useContext, useEffect } from "react";
import { io } from "socket.io-client";
import { UserContext } from "../context/UserContext";
import { LoginContext } from "../context/LoginContext";
import { ServerContext } from "../context/ServerContext";
import styles from "./Chat.module.css";
import ChatBar from "./ChatBar";
import { useParams } from "react-router-dom";

const socket = io("http://localhost:4000/", {
  transports: ["websocket", "polling"],
});

socket.on("message", (message) => {
  console.log(message);
});

const Chat = () => {
  const { serverList, setServerList } = useContext(ServerContext);
  const { isLoggedIn, setIsLoggedIn } = useContext(LoginContext);
  const { loginInfo, setLoginInfo } = useContext(UserContext);
  const [message, setMessage] = useState("");

  const [showServer, setShowServer] = useState(null);

  const { id } = useParams();

  const handleMouseEnter = (id) => {
    setShowServer(id);
  };
  const handleMouseLeave = (id) => {
    setShowServer(null);
  };

  const handleSendMessage = (e) => {
    if (e.key === "Enter") {
      socket.emit("message", { message, id });
      e.target.value = "";
    }
  };

  useEffect(() => {
    socket.emit("join", id);
  }, [id]);

  return (
    <div className={styles.chat}>
      <div className={styles.insideapp}>
        <div className={styles.server}>
          <ChatBar />
        </div>
        <div className={styles.leftside}>
          {serverList.map((server) => {
            return (
              id === server.name &&
              server.channels.map((channel) => {
                return (
                  <div key={channel.id} className={styles.channel}>
                    <h3># {channel.name}</h3>
                  </div>
                );
              })
            );
          })}
        </div>
        <div className={styles.middle}>
          <input
            onChange={(e) => {
              setMessage(e.target.value);
            }}
            onKeyPress={(e) => handleSendMessage(e)}
            className={styles.textBox}
            type="text"
          />
        </div>
        <div className={styles.rightside}></div>
      </div>
    </div>
  );
};

export default Chat;
