import nodemon from "nodemon";
import React, { useState, useContext } from "react";
import styles from "./ChatBar.module.css";

const ChatBar = () => {
  const [serverList, setServerList] = useState([
    {
      id: 1,
      name: "Public",
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT8479Ucg3dNkErrscpa3BemM8RUp9Z68HV7w4TJpEL1meuvgETwTvYdJXWm5A1cqkcFA8&usqp=CAU",
    },
    {
      id: 2,
      name: "Fortnite",
      image: "https://i.imgur.com/13RZOhj.gif",
    },
  ]);

  const [showServer, setShowServer] = useState(null);

  const handleMouseEnter = (id) => {
    setShowServer(id);
  };
  const handleMouseLeave = (id) => {
    setShowServer(null);
  };

  const handleClick = (e) => {};

  return (
    <div className={styles.ChatBar}>
      {serverList.map((server) => {
        return (
          <div
            onClick={(e) => handleClick(e)}
            className={styles.serverIcon}
            id={server.id}
            key={server.id}
          >
            <img
              onMouseEnter={() => handleMouseEnter(server.id)}
              onMouseLeave={() => handleMouseLeave(server.id)}
              src={server.image}
              alt=""
            />

            {server.id === showServer && (
              <div className={styles.serverIconNameBackground}>
                <p className={styles.serverIconName}>{server.name}</p>
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default ChatBar;
